<script>
    export default {
        props: {
            mono: String
        },
        computed: {
            kalamazooFill() {
                if (this.mono) {
                    switch (this.mono) {
                        case 'white':
                            return "#ffffff";
                        case 'black':
                            return "#000000";
                    }
                }
                return "#000000";
            },
            valleyFill() {
                if (this.mono) {
                    switch (this.mono) {
                        case 'white':
                            return "#ffffff";
                        case 'black':
                            return "#000000";
                    }
                }
                return "#2b6cb0";
            }
        }
    }
</script>